package AllFromTheBeginning;

public class IamFortyTwoProgramArrayContinuation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int no[]= {1,-2,30};
		int sum=0;
		for(int i=0;i<=no.length-1;i++)
		{
			sum=sum+no[i];
		}
		System.out.println("result:"+sum);

	}

}
